package com.dtac.employee.action;
import java.util.List;

import javax.servlet.http.HttpServletRequest;

import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.dtac.admin.data.DBLogin;
import com.dtac.admin.data.DBMemberType;
import com.dtac.admin.data.DBMonth;
import com.dtac.admin.data.DBYear;
import com.dtac.employee.form.TimeAttendForm;
import com.dtac.utils.DateUtil;
/** 
 * MyEclipse Struts
 * Creation date: 23-08-2012
 */
public class TimeAttendReport1StartAction extends Action {

	public ActionForward execute(ActionMapping mapping, ActionForm form,
		HttpServletRequest request, HttpServletResponse response) 
	throws Exception { //23-08-2012
		String forwardText  = "success";
		String loginId 		= "";
		String appCode  	= "hrr2";
		try {
			HttpSession session = request.getSession();
			if (session.isNew()) {
				session.invalidate();
				request.setAttribute("alertMessage","Session Timeout. Login again.");
				return mapping.findForward("relogin");
			} else {
				loginId = (String) session.getAttribute("loginId");
				if (loginId == null) {
					request.setAttribute("alertMessage","Please Login.");
					return mapping.findForward("relogin");
				}
			}
			DBLogin dbLogin = new DBLogin();			
			if (!dbLogin.CheckAppAuth(loginId,appCode,"disp")) {
				request.setAttribute("alertMessage","You don't have authorize.");
				return mapping.findForward("alertmsg");
			}
			
			TimeAttendForm timeAttendForm = (TimeAttendForm) form;
			timeAttendForm.initial();
			String empTypeCode 	= "ST";
			String empDeptCode 	= "";
			String fromDate		= "";
			String toDate		= "";
			String groupName	= "";
			String year			= "";			 
			String month		= "";

			if (session.getAttribute("year") != null) 	year = (String) session.getAttribute("year");
			if (session.getAttribute("month") != null) 	month = (String) session.getAttribute("month");
			if (session.getAttribute("groupName") != null) groupName = (String) session.getAttribute("groupName");			
			if (session.getAttribute("empTypeCode") != null) empTypeCode = (String) session.getAttribute("empTypeCode");
			if (session.getAttribute("empDeptCode") != null) empDeptCode = (String) session.getAttribute("empDeptCode");
			if (session.getAttribute("fromDate") != null) fromDate = (String) session.getAttribute("fromDate");
			if (session.getAttribute("toDate") != null) toDate = (String) session.getAttribute("toDate");

			DateUtil dateUtil = new DateUtil();
			if (fromDate.equals("")) fromDate = dateUtil.CnvToDDMMYYYYThaiYear(dateUtil.CnvToYYYYMMDD(dateUtil.curDate(), '-'));
			if (toDate.equals("")) toDate = dateUtil.CnvToDDMMYYYYThaiYear(dateUtil.CnvToYYYYMMDD(dateUtil.curDate(), '-'));
			if (year.equals("")) year 	= dateUtil.curTHYear();				
			if (month.equals(""))month	= dateUtil.curMonth();				
			
			DBMemberType dbMemberType = new DBMemberType();
			List memberTypeList = dbMemberType.GetMemberTypeList("", "", "");
			if (memberTypeList.size() > 0) request.setAttribute("memberTypeList", memberTypeList);
						
			DBYear dbYear 	= new DBYear();
			List yearList	= dbYear.GetYearList("", "");
			if (yearList.size() > 0) request.setAttribute("yearList", yearList);
			
			DBMonth dbMonth = new DBMonth();
			List monthList = dbMonth.GetMonthList();
			if (monthList.size() > 0) request.setAttribute("monthList", monthList);

			timeAttendForm.setEmpTypeCode(empTypeCode);
			timeAttendForm.setEmpDeptCode(empDeptCode);
			timeAttendForm.setGroupName(groupName);
			timeAttendForm.setFromDate(fromDate);
			timeAttendForm.setToDate(toDate);
			timeAttendForm.setYear(year);
			timeAttendForm.setMonth(month);
			//timeAttendForm.setReportNo("1");			
		} catch (Exception e) {
			throw new Exception(e.getMessage());
		}	
		return mapping.findForward(forwardText);
	}
}