package com.dtac.inventory.action;
import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.dtac.admin.data.DBLogin;
import com.dtac.admin.data.DBMasterTable;
import com.dtac.inventory.data.DBCustomer;
import com.dtac.inventory.form.CustomerForm;
/** 
 * MyEclipse Struts
 * Creation date: 12-04-2012
 */
public class CustomerListStartAction extends Action {

	public ActionForward execute(ActionMapping mapping, ActionForm form,
		HttpServletRequest request, HttpServletResponse response) 
	throws Exception { //18-04-2012
		String forwardText  = "success";
		String loginId 		= "";
		String appCode  	= "mm07";
		try {
			HttpSession session = request.getSession();
			if (session.isNew()) {
				session.invalidate();
				request.setAttribute("alertMessage","Session Timeout. Login again.");
				return mapping.findForward("relogin");
			} else {
				loginId = (String) session.getAttribute("loginId");
				if (loginId == null) {
					request.setAttribute("alertMessage","Please Login.");
					return mapping.findForward("relogin");
				}
			}
			DBLogin dbLogin = new DBLogin();			
			if (!dbLogin.CheckAppAuth(loginId,appCode,"disp")) {
				request.setAttribute("alertMessage","You don't have authorize.");
				return mapping.findForward("alertmsg");
			}
			
			CustomerForm CustomerForm = (CustomerForm) form;

			String customerCode = "";
			if (session.getAttribute("customerCode") != null) customerCode = (String) session.getAttribute("customerCode");

			DBCustomer dbVender = new DBCustomer();
			List customerList = dbVender.GetCustomerMasterList("", "", "", "", "");
			if (customerList.size() > 0) request.setAttribute("customerList", customerList);

			DBMasterTable dbMasterTable = new DBMasterTable();
			List custTypeList = dbMasterTable.GetMasterTableDTList("vdty", "", "", "AC");
			if (custTypeList.size() > 0) request.setAttribute("custTypeList", custTypeList);
			
			CustomerForm.setcustomerCode(customerCode);	
			CustomerForm.setStatus("AC");
			
			CustomerForm.initial();
		} catch (Exception e) {
			throw new Exception(e.getMessage());
		}	
		return mapping.findForward(forwardText);
	}
}